mapper.select:
select

,sys_dept.dept_name
from sys_user left join sys_dept on sys_user.dept_id=sys_dept.id_
where 1=1
and usable_=#{usable}
and account_=#{account}
and password_=#{password}

	and (account_ like CONCAT('%',#{keyword},'%') or user_name like CONCAT('%',#{keyword},'%') or phone_ like CONCAT('%',#{keyword},'%'))
